CREATE TABLE `PHP-Login-System`.`users` ( `user_id` INT(5) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT 'user id' , `email` VARCHAR(100) NOT NULL COMMENT 'user\'s email address' , `password` VARCHAR(200) NOT NULL COMMENT 'user\'s password' , `reg_time` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'time and date the user registered' , PRIMARY KEY (`user_id`), UNIQUE (`email`)) ENGINE = InnoDB COMMENT = 'users table for PHP-Login-System';